changeSet.sql:
INSERT INTO http_log (id, data, send_oeplog_id)
SELECT nextval('http_log_seq'), send_data, id
FROM oeplog;

INSERT INTO http_log (id, data, receive_oeplog_id)
SELECT nextval('http_log_seq'), receive_data, id
FROM oeplog;

INSERT INTO soap_packet (
    id,
    casenumber,
    date,
    exchangetype,
    originrequestidref,
    originator,
    recipient,
    requestidref,
    sender,
    service,
    servicecode,
    status,
    typecode,
    send_oeplog_id
)
SELECT nextval('soap_packet_seq'),
    send_casenumber,
    send_date,
    send_exchangetype,
    send_originrequestidref,
    send_originator,
    send_recipient,
    send_requestidref,
    send_sender,
    send_service,
    send_servicecode,
    send_status,
    send_typecode,
    id
FROM oeplog;

